import path from 'path';
import { load } from 'dotenv-safe';

// import .env variables
if (process.env.NODE_ENV !== 'production') {
  load({
    path: path.join(__dirname, '../../.env'),
    sample: path.join(__dirname, '../../.env.example'),
  });
}

module.exports = {
  env: process.env.NODE_ENV,
  port: process.env.PORT,
  jwtSecret: process.env.JWT_SECRET,
  jwtExpirationInterval: process.env.JWT_EXPIRATION_MINUTES,
  logs: process.env.NODE_ENV === 'production' ? 'combined' : 'dev',
  clientUrl: process.env.CLIENT_URL,
  certificateSecret: process.env.CERTIFICATE_SECRET,
  teIntegrationServiceEndpoint: process.env.TE_INTEGRATION_ENDPOINT,
  teAdminEndpoint: process.env.TE_ADMIN_ENDPOINT,
  gcloudProjectId: process.env.GCLOUD_PROJECT_ID,
  gcloudClientEmail: process.env.GCLOUD_CLIENT_EMAIL,
  gcloudPrivateKey: process.env.GCLOUD_PRIVATE_KEY,
  gcloudBucket: process.env.GLCOUD_BUCKET,
  teAppId: process.env.TE_APP_ID,
  TE_APP_ENV: process.env.TE_APP_ENV,
  TE_APP_NAME: process.env.TE_APP_NAME,
  TE_VIEWER_ENDPOINT: process.env.TE_VIEWER_ENDPOINT,
  TE_APPS_EMAIL_ACCESS_TOKEN: process.env.TE_APPS_EMAIL_ACCESS_TOKEN,
  TE_APPS_EMAIL_ENDPOINT: process.env.TE_APPS_EMAIL_ENDPOINT,
  GOOGLE_DEVELOP_API_KEY: process.env.GOOGLE_DEVELOP_API_KEY,
  PROJECT_ID: process.env.PROJECT_ID,
  GCLOUD_LOGGING_CLIENT_EMAIL: process.env.GCLOUD_LOGGING_CLIENT_EMAIL,
  GCLOUD_LOGGING_PRIVATE_KEY: process.env.GCLOUD_LOGGING_PRIVATE_KEY,
};

