sourcesContent:
/* eslint no-param-reassign: "error" */
import mongoose from 'mongoose';
import httpStatus from 'http-status';
import moment from 'moment';
import { pick, get as _get, union, assign } from 'lodash';
import APIError from '../../utils/APIError';
import { formStatusTypes, formInstanceStatusTypes, FORM_INSTANCE_ACCEPTANCE_STATUS } from '../../../lib/enums';
import TEAdminService from '../../services/adminService';
import { generateQueryFormByTypeOrPeriod } from '../../utils/helpers';
import { getRecipientGroupModel } from '../recipientGroup/recipientGroup.model';
import { DB_SERVICE_PREFIX } from '@timeedit/be-utils/lib/config/configBe';

/**
 *
 * Form transform fields
 */
const transformFields = [
  'name',
  'ownerId',
  'assigners',
  'description',
  'schedulingTutorial',
  'sections',
  'status',
  'dueDate',
  'formPeriod',
  'allowLateResponses',
  'allowToCreateNewPrimaryObject',
  'sendAutomaticReminders',
  'objectScope',
  'primaryObjectFields',
  'primaryObjectDescription',
  'scopedObjectFilters',
  'reservationMode',
  'excludedObjects',
  'submitterMustSelectObject',
  'submitterCanSubmitMultiple',
  'allowLinkSharing',
  'rollOverFormConfig',
  'holidayConfigs',
  'excludeDatesConfig',
  'formType',
  'enableSemesterWeek',
  'featureSettings',
  'createdAt',
  'updatedAt',
  'deletedAt',
  'hideScopedObjectInSameTypeDataSourceSelectors',
  'allowMultipleSubmissionsInSameObject',
  'allowCollaboratorsToSubmit',
];

async function get(id) {
  try {
    let form;

    if (mongoose.Types.ObjectId.isValid(id)) {
      form = await this.findOne({ _id: id });
    }
    if (form) {
      return form;
    }

    throw new APIError({
      message: 'Form does not exist',
      status: httpStatus.NOT_FOUND,
    });
  } catch (error) {
    throw error;
  }
}

async function list({ page = 1, perPage = 1000, userId, formType, startDate, endDate }) {
  const matchQuery = {
    ...generateQueryFormByTypeOrPeriod({ formType, startDate, endDate }),
  };

  if (userId) {
    matchQuery.assigners = { $in: [new mongoose.Types.ObjectId(userId)] };
  }

  // get userIds to query user information from te admin
  let userIds = await this.aggregate([{ $match: matchQuery }, { $group: { _id: '$ownerId' } }]);

  userIds = userIds.map(({ _id }) => _id);
  // get form information
  const getForms = await this.aggregate([
    { $match: matchQuery },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-forminstances`,
        let: { formId: '$_id' },
        pipeline: [
          {
            $match: {
              $expr: {
                $and: [{ $eq: ['$formId', '$$formId'] }, { $eq: [formInstanceStatusTypes.SUBMITTED, '$status'] }],
              },
            },
          },
          { $project: { _id: 1 } },
        ],
        as: 'submitted',
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-forminstances`,
        let: { formId: '$_id' },
        pipeline: [
          {
            $match: {
              $expr: {
                $and: [{ $eq: ['$formId', '$$formId'] }, { $eq: [formInstanceStatusTypes.DRAFT, '$status'] }],
              },
            },
          },
          { $project: { _id: 1 } },
        ],
        as: 'draft',
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-forminstances`,
        let: { formId: '$_id' },
        pipeline: [
          {
            $match: {
              $expr: {
                $and: [{ $eq: ['$formId', '$$formId'] }, { $eq: [formInstanceStatusTypes.OPENED, '$status'] }],
              },
            },
          },
          { $project: { _id: 1 } },
        ],
        as: 'opened',
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-forminstances`,
        let: { formId: '$_id' },
        pipeline: [
          {
            $match: {
              $expr: {
                $and: [{ $eq: ['$formId', '$$formId'] }, { $eq: [formInstanceStatusTypes.SENT, '$status'] }],
              },
            },
          },
          { $project: { _id: 1 } },
        ],
        as: 'sent',
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-forminstances`,
        let: { formId: '$_id' },
        pipeline: [
          {
            $match: {
              $expr: {
                $and: [
                  { $eq: ['$formId', '$$formId'] },
                  {
                    $eq: [FORM_INSTANCE_ACCEPTANCE_STATUS.REJECTED, '$teCoreProps.acceptanceStatus'],
                  },
                ],
              },
            },
          },
          { $project: { _id: 1 } },
        ],
        as: 'declined',
      },
    },
    {
      $project: {
        _id: 1,
        status: 1,
        name: 1,
        description: 1,
        schedulingTutorial: 1,
        sections: 1,
        ownerId: 1,
        assigners: 1,
        dueDate: 1,
        formPeriod: 1,
        allowLateResponses: 1,
        allowToCreateNewPrimaryObject: 1,
        sendAutomaticReminders: 1,
        objectScope: 1,
        primaryObjectFields: 1,
        primaryObjectDescription: 1,
        scopedObjectFilters: 1,
        reservationMode: 1,
        excludedObjects: 1,
        submitterMustSelectObject: 1,
        submitterCanSubmitMultiple: 1,
        allowLinkSharing: 1,
        formType: 1,
        holidayConfigs: 1,
        rollOverFormConfig: 1,
        responses: {
          [formInstanceStatusTypes.SENT]: { $size: '$sent' },
          [formInstanceStatusTypes.OPENED]: { $size: '$opened' },
          [formInstanceStatusTypes.DRAFT]: { $size: '$draft' },
          [formInstanceStatusTypes.SUBMITTED]: { $size: '$submitted' },
          [formInstanceStatusTypes.DECLINED]: { $size: '$declined' },
        },
        excludeDatesConfig: 1,
        enableSemesterWeek: 1,
        createdAt: 1,
        updatedAt: 1,
        deletedAt: 1,
        featureSettings: 1,
      },
    },
    { $skip: Number(perPage) * (page - 1) },
    { $limit: Number(perPage) },
  ]);
  const getFormsCount = await this.countDocuments(matchQuery).exec();

  return {
    forms: getForms,
    count: getFormsCount,
    userIds,
  };
}

async function create(payload) {
  const form = new this({ ...payload });
  const newForm = await form.save({ checkKeys: false });
  return newForm;
}

async function update(formId, payload) {
  const form = await this.findOne({ _id: formId });
  const params = pick(payload, transformFields);
  const formToUpdate = assign(form, params);
  formToUpdate.sections = params.sections;
  await formToUpdate.save();
  return pick(formToUpdate, transformFields.concat(['_id']));
}

function copyForm(form, cloneOptions) {
  const { sections, createdAt, updatedAt, ...rest } = form;
  const sectionHasOldActivitiyTemplateIdx = sections.findIndex((section) => {
    return (
      section.activityTemplatesSettings &&
      section.activityTemplatesSettings.isEnabled &&
      !section.activityTemplatesSettings.enableNewActivityTemplateDesign
    );
  });

  if (sectionHasOldActivitiyTemplateIdx > -1) {
    const sectionContainsNumberOfStudents = sections.find(
      (section) => section.pathwayElementMappingsRegular?.numberOfStudentsElementId,
    );
    const sectionHasOldActivitiyTemplate = sections[sectionHasOldActivitiyTemplateIdx];
    const maxClassSizeElementId =
      sectionHasOldActivitiyTemplate.activitySettings?.maxClassSizeElementId ||
      sectionHasOldActivitiyTemplate.pathwayElementMappings?.maxClassSizeElementId;
    const numberOfStudentsElementId =
      sectionHasOldActivitiyTemplate.activitySettings?.numberOfStudentsElementId ||
      sectionContainsNumberOfStudents?.pathwayElementMappingsRegular.numberOfStudentsElementId;
    sectionHasOldActivitiyTemplate.activitySettings = {
      ...(sectionHasOldActivitiyTemplate.activitySettings || {}),
      tracksSettings: {
        isEnabled: true,
        title: 'Tracks',
      },
      isEnabled: true,
      autoCalculation: maxClassSizeElementId && numberOfStudentsElementId ? 'MAX_CLASS_SIZE' : 'OFF',
      maxClassSizeElementId,
      numberOfStudentsElementId,
    };
    sectionHasOldActivitiyTemplate.activityTemplatesSettings.enableNewActivityTemplateDesign = true;
    sectionHasOldActivitiyTemplate.activityTemplatesSettings.allowEditActivityTemplateName = true;
  }

  return {
    ...rest,
    sections,
    ...(cloneOptions || {}),
    allowLinkSharing: false,
    dueDate: moment(form.dueDate).isBefore(moment()) ? undefined : form.dueDate,
    status: formStatusTypes.FORM_STATUS_DRAFT,
    _id: undefined,
    source: undefined,
  };
}

async function clone(formId, cloneOptions) {
  const form = await this.get(formId);
  const newForm = copyForm(form.toObject(), cloneOptions);
  const cloneForm = await this.create(newForm);
  return cloneForm;
}

async function updateStatus(id, status) {
  try {
    const statusMapping = {
      draft: formStatusTypes.FORM_STATUS_DRAFT,
      active: formStatusTypes.FORM_STATUS_ACTIVE,
      archived: formStatusTypes.FORM_STATUS_ARCHIVED,
    };
    await this.findOneAndUpdate(
      { _id: id },
      {
        status: statusMapping[status],
      },
    );
    return statusMapping[status];
  } catch (error) {
    throw new APIError({ message: error });
  }
}

async function getAllDataForms(
  {
    // page = 1,
    // perPage = 30,
    organizationId,
  },
  accessToken,
) {
  try {
    const forms = await this.aggregate([
      {
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-forminstances`,
          let: { formId: '$_id' },
          pipeline: [
            {
              $match: {
                $expr: {
                  $and: [{ $eq: ['$formId', '$$formId'] }, { $eq: [formInstanceStatusTypes.SUBMITTED, '$status'] }],
                },
              },
            },
          ],
          as: 'formInstances',
        },
      },
      {
        $unwind: {
          path: '$formInstances',
          preserveNullAndEmptyArrays: true,
        },
      },
      {
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-recipients`,
          let: { recipientId: '$formInstances.recipientId' },
          pipeline: [{ $match: { $expr: { $eq: ['$_id', '$$recipientId'] } } }, { $project: { email: 1, userId: 1 } }],
          as: 'formInstances.recipient',
        },
      },
      {
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-elementvalues`,
          localField: 'formInstances._id',
          foreignField: 'formInstanceId',
          as: 'formInstances.values',
        },
      },
      {
        $unwind: {
          path: '$formInstances.recipient',
          preserveNullAndEmptyArrays: true,
        },
      },
      {
        $group: {
          _id: '$_id',
          status: { $first: '$status' },
          name: { $first: '$name' },
          description: { $first: '$description' },
          sections: { $first: '$sections' },
          ownerId: { $first: '$ownerId' },
          assigners: { $first: '$assigners' },
          createdAt: { $first: '$createdAt' },
          dueDate: { $first: '$dueDate' },
          sendAutomaticReminders: { $first: '$sendAutomaticReminders' },
          submitterMustSelectObject: { $first: '$submitterMustSelectObject' },
          submitterCanSubmitMultiple: { $first: '$submitterCanSubmitMultiple' },
          allowLinkSharing: { $first: '$allowLinkSharing' },
          formType: { $first: '$formType' },
          holidayConfigs: { $first: '$holidayConfigs' },
          formInstances: {
            $push: {
              $cond: [
                {
                  $eq: [{ $size: '$formInstances.values' }, 0],
                },
                undefined,
                '$formInstances',
              ],
            },
          },
        },
      },
      // { $skip: Number(perPage) * (page - 1) },
      // { $limit: Number(perPage) },
    ]);
    let userIds = [];
    forms.forEach((form) => {
      userIds = union(userIds, [form.ownerId.toString()]);
      _get(form, 'formInstances', []).forEach((formInstance) => {
        userIds = union(userIds, [_get(formInstance, 'recipient.userId', '').toString()]);
      });
    });
    userIds = userIds.filter((userId) => userId !== '');
    const adminService = new TEAdminService(organizationId, accessToken);
    const users = await adminService.getUserByIds({
      userIds,
      fields: ['firstName', 'lastName'],
    });
    return { forms, users };
  } catch (error) {
    throw new APIError({ message: error });
  }
}

async function listRecipientBelongsToForms(options, organizationId) {
  const recipients = await this.aggregate([
    { $match: options },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-recipients`,
        localField: '_id',
        foreignField: 'assignedForms.formId',
        as: 'recipients',
      },
    },
    {
      $unwind: {
        path: '$recipients',
      },
    },
    {
      $group: {
        _id: '$recipients._id',
        isUser: { $first: '$recipients.isUser' },
        email: { $first: '$recipients.email' },
        assignedForms: { $first: '$recipients.assignedForms' },
        firstName: { $first: '$recipients.firstName' },
        lastName: { $first: '$recipients.lastName' },
        createdAt: { $first: '$recipients.createdAt' },
        updatedAt: { $first: '$recipients.updatedAt' },
      },
    },
  ]);

  const RecipientGroupModel = await getRecipientGroupModel(organizationId);
  const recipientsFromGroup = await RecipientGroupModel.aggregate([
    { $match: options },
    {
      $unwind: {
        path: '$recipientIds',
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-recipients`,
        localField: 'recipientIds',
        foreignField: '_id',
        as: 'recipients',
      },
    },
    {
      $unwind: {
        path: '$recipients',
      },
    },
    {
      $group: {
        _id: '$recipients._id',
        isUser: { $first: '$recipients.isUser' },
        email: { $first: '$recipients.email' },
        assignedForms: { $first: '$recipients.assignedForms' },
        firstName: { $first: '$recipients.firstName' },
        lastName: { $first: '$recipients.lastName' },
        createdAt: { $first: '$recipients.createdAt' },
        updatedAt: { $first: '$recipients.updatedAt' },
      },
    },
  ]);

  recipientsFromGroup.forEach((item) => {
    const isExisting = recipients.find((recipient) => recipient._id.equals(item._id));
    if (!isExisting) recipients.push(item);
  });

  return recipients;
}

async function getAllFormInstances({ createdAt, statusQuery }) {
  try {
    const forms = await this.aggregate([
      {
        $match: statusQuery,
      },
      {
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-forminstances`,
          let: { formId: '$_id' },
          pipeline: [
            {
              $match: {
                $expr: {
                  $eq: ['$formId', '$$formId'],
                },
                createdAt,
              },
            },
            {
              $project: {
                values: 0,
                activityTemplateSettings: 0,
                jointTeachingSettings: 0,
                groupManagementSettings: 0,
              },
            },
          ],
          as: 'formInstances',
        },
      },
      {
        $match: {
          $expr: {
            $gte: [{ $size: '$formInstances' }, 1],
          },
        },
      },
      {
        $unwind: {
          path: '$formInstances',
          preserveNullAndEmptyArrays: true,
        },
      },
      {
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-recipients`,
          let: { recipientId: '$formInstances.recipientId' },
          pipeline: [
            { $match: { $expr: { $eq: ['$_id', '$$recipientId'] } } },
            { $project: { email: 1, firstName: 1, lastName: 1 } },
          ],
          as: 'formInstances.recipient',
        },
      },
      {
        $set: {
          'formInstances.recipient': {
            $first: '$formInstances.recipient',
          },
        },
      },
      {
        $addFields: {
          'formInstances.otherRecipientIds': {
            $cond: {
              if: {
                $ne: [{ $type: '$formInstances.otherRecipientIds' }, 'array'],
              },
              then: [],
              else: '$formInstances.otherRecipientIds',
            },
          },
        },
      },
      {
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-recipient`,
          let: {
            otherRecipientIds: '$formInstances.otherRecipientIds',
          },
          pipeline: [
            {
              $match: {
                $expr: {
                  $in: ['$_id', '$$otherRecipientIds'],
                },
              },
            },
            {
              $project: {
                email: 1,
                firstName: 1,
                lastName: 1,
              },
            },
          ],
          as: 'formInstances.otherRecipients',
        },
      },
      {
        $group: {
          _id: '$_id',
          status: { $first: '$status' },
          name: { $first: '$name' },
          objectScope: { $first: '$objectScope' },
          formInstances: {
            $push: '$formInstances',
          },
        },
      },
    ]);

    return { forms };
  } catch (error) {
    throw new APIError({ message: error });
  }
}

export default function (schema) {
  schema.statics = {
    ...this,
    transformFields,
    get,
    list,
    create,
    update,
    clone,
    updateStatus,
    getAllDataForms,
    listRecipientBelongsToForms,
    getAllFormInstances,
    copyForm,
  };
}

