export const formStatusTypes = {
  FORM_STATUS_DRAFT: 'FORM_STATUS_DRAFT',
  FORM_STATUS_ACTIVE: 'FORM_STATUS_ACTIVE',
  FORM_STATUS_ARCHIVED: 'FORM_STATUS_ARCHIVED',
};

/**
 * TE Core - manual scheduling statuses
 */
export const teCoreManualSchedulingStatuses = {
  COMPLETED: 'COMPLETED',
  NOT_COMPLETED: 'NOT_COMPLETED',
};

/**
 * TE Core - timing modes
 */
export const teCoreTimingModes = {
  EXACT: 'EXACT',
