/* eslint-disable prefer-destructuring */
/* eslint no-irregular-whitespace: "off" */
import httpStatus from 'http-status';
import axios from 'axios';
import moment from 'moment';
import { encode } from 'jwt-simple';
import { get } from 'lodash';
import { teAdminEndpoint, teAppId, jwtSecret } from '../../config/vars';
import APIError from '../utils/APIError';
import { extractRegionFromToken } from '../utils/regionFromToken';
import _ from 'lodash';

export const generateAuthTokenByRegion = (region) => {
  const userData = {
    region,
    organizationIds: [],
    authStrategy: 'inherit',
    organizationGroups: [
      {
        isActive: true,
        app: 'teAdmin',
        organizationGroup: 'root',
        permissionGroup: 'teAdmin:TE_Admin',
      },
    ],
    permissions: ['teAdmin:administrator', 'teAdmin:organization:get', 'teAdmin:root'],
    isActive: true,
  };
  const encodedUserData = encode(
    {
      region,
      userData,
      exp: moment().add(1, 'day').unix(), // Set 1 day for now
      iat: moment().unix(),
    },
    jwtSecret,
  );
  return encodedUserData;
};

export const generateAuthToken = (accessToken, regionParam) => {
  const region = regionParam ?? extractRegionFromToken(accessToken);
  return generateAuthTokenByRegion(region);
};

export default class TEAdminService {
  constructor(organizationId, accessToken, regionParam /* Either accessToken or regionParam should be defined */) {
    const encodedUserData = generateAuthToken(accessToken, regionParam);
    this.endpoint = teAdminEndpoint;
    this.organizationId = organizationId;
    this.app = 'tePref';
    this.headers = {
      'Content-Type': 'application/json',
      Authorization: `Bearer ${encodedUserData}`,
    };
  }

  async getOrganization() {
    const { data } = await this.callAPI({
      method: 'GET',
      path: `/v1/organizations/${this.organizationId}`,
    });
    return get(data, 'organization', {});
  }

  async getAllOrganizations() {
    const { data } = await this.callAPI({
      method: 'GET',
      path: `/v1/organizations`,
    });
    return get(data, 'organizations', []);
  }

  async updateOrganization(payload) {
    const { data } = await this.callAPI({
      method: 'PUT',
      path: `/v1/organizations/${this.organizationId}`,
      payload,
    });
    return get(data, 'organization', {});
  }

  async getAppSettings() {
    const { data } = await this.callAPI({
      method: 'GET',
      path: `/v1/organizations/${this.organizationId}/app-settings/${teAppId}`,
    });
    return get(data, 'appSetting', {});
  }

  async updateOrgAppSettings(payload) {
    const { data } = await this.callAPI({
      method: 'PUT',
      path: `/v1/organizations/${this.organizationId}/app-settings/${teAppId}`,
      payload,
    });
    return get(data, 'appSetting', {});
  }

  async createUser(payload) {
    const { data } = await this.callAPI({
      method: 'POST',
      path: `/v1/organizations/${this.organizationId}/users`,
      payload,
    });
    return get(data, 'user', {});
  }

  async updateUser(userId, payload) {
    const { data } = await this.callAPI({
      method: 'PUT',
      path: `/v1/organizations/${this.organizationId}/users/${userId}`,
      payload,
    });
    return get(data, 'user', {});
  }

  async getUserByIds(payload) {
    const { data } = await this.callAPI({
      method: 'POST',
      path: `/v1/organizations/${this.organizationId}/users/get/ids`,
      payload,
    });
    return get(data, 'users', []);
  }

  async getUserByEmail(payload) {
    const { data } = await this.callAPI({
      method: 'POST',
      path: `/v1/organizations/${this.organizationId}/users/get/email`,
      payload,
    });
    return get(data, 'user', {});
  }

  async getUsers() {
    const { data } = await this.callAPI({
      method: 'GET',
      path: `/v1/organizations/${this.organizationId}/users?app=${this.app}`,
    });

    return get(data, 'users', []);
  }

  async getUser(userId) {
    const { data } = await this.callAPI({
      method: 'GET',
      path: `/v1/users/${userId}`,
    });
    return get(data, 'user', {});
  }

  async activeUser(userId) {
    const { data } = await this.callAPI({
      method: 'PUT',
      path: `/v1/organizations/${this.organizationId}/users/${userId}/active`,
    });
    return get(data, 'user', {});
  }

  async deactiveUser(userId) {
    const { data } = await this.callAPI({
      method: 'PUT',
      path: `/v1/organizations/${this.organizationId}/users/${userId}/deactive`,
    });
    return get(data, 'user', {});
  }

  async sendEmailRemindSubmission(payload) {
    await this.callAPI({
      method: 'POST',
      path: '/v1/email/remind-submission',
      payload: {
        ..._.pick(payload, [
          'email',
          'userId',
          'firstName',
          'lastName',
          'formUrl',
          'formName',
          'assignedBy',
          'subject',
          'language',
        ]),
        assignedBy: payload.assignedBy || '',
        assignedScopedObject: payload.scopedObjectLabel,
      },
    });
  }

  async sendEmailAssigner(payload) {
    await this.callAPI({
      method: 'POST',
      path: '/v1/email/form-assigner',
      payload,
    });
  }

  async callAPI({ method, path, payload }) {
    try {
      const url = `${this.endpoint}${path}`;
      const { data } = await axios({
        method,
        url,
        data: payload,
        headers: this.headers,
      });
      return data;
    } catch (error) {
      const status = get(error.response, 'data.code', error.status || httpStatus.INTERNAL_SERVER_ERROR);
      const message = `TE-ADMIN: ${get(error.response, 'data.message', error.message || 'Fail to call')}`;
      return this.failedServiceCall({ status, message });
    }
  }

  // eslint-disable-next-line class-methods-use-this
  failedServiceCall(error) {
    throw new APIError({
      status: error.status || httpStatus.INTERNAL_SERVER_ERROR,
      message: error.message || httpStatus[`${httpStatus.INTERNAL_SERVER_ERROR}_MESSAGE`],
    });
  }

  set region(region) {
    const encodedUserData = generateAuthTokenByRegion(region);
    this.headers = {
      'Content-Type': 'application/json',
      Authorization: `Bearer ${encodedUserData}`,
    };
  }
}

