import { decode } from 'jwt-simple';
import logger from '../../config/logger';
import { jwtSecret } from '../../config/vars';

export const extractRegionFromToken = (accessToken: string): string => {
  let region;
  if (accessToken) {
    try {
      const parsedToken = decode(accessToken.split(' ')[1], jwtSecret);
      region = parsedToken.region;
    } catch (err) {
      const errorsOnlyWarnings = ['Error: Signature verification failed', 'Error: Token expired'];
      if (errorsOnlyWarnings.includes((err as any)?.toString())) {
        logger.warn(err);
      } else {
        logger.error(err);
      }
    }
  }
  return region;
};

