sourcesContent:
/* eslint no-useless-escape: 0 */
import { isEmpty } from 'lodash';
import moment from 'moment';
import Emails from './emails';

export const queryEmailOrUserId = ({ userId, email }) => ({
  $or: [
    {
      $and: [{ userId }, { userId: { $ne: null } }],
    },
    {
      $and: [{ email }, { email: { $ne: null } }],
    },
  ],
});

export const isEmailAction = (email) => {
  const re =
    /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  return re.test(String(email).toLowerCase());
};

export const generateFullName = (value = {}) => {
  if (!value.firstName || !value.lastName) return 'N/A';
  return `${value.firstName} ${value.lastName}`;
};

export const sendingEmailToAllImportedRecipients = ({
  form,
  organization,
  updatedFormInstances,
  loggedInUser,
  recipients,
  mappedRecipientToRecipientOwnerId,
  scopedObjectLabel,
}) => {
  const mappedRecipientToId = recipients.reduce((result, r) => ({ ...result, [r._id]: r }), {});
  const sendEmail = new Emails(loggedInUser.region);
  const listEmails = [];

  return new Promise((resolve) => {
    updatedFormInstances.forEach((formInstance) => {
      const id = `${formInstance.recipient._id.toString()}${formInstance.scopedObject}`;

      if (!isEmpty(mappedRecipientToRecipientOwnerId[id])) {
        mappedRecipientToRecipientOwnerId[id].forEach((r) => {
          listEmails.push({
            email: mappedRecipientToId[r].email,
            formInstanceId: formInstance._id,
            assignBy: generateFullName(formInstance.assignedBy),
            pinCode: mappedRecipientToId[r].pinCode,
            formName: form.name,
            formDescription: form.description,
            organizationName: organization.name,
            scopedObjectLabel,
            assignedScopedObject: formInstance.scopedObjectLabel,
            language: organization.primaryLanguage,
          });
        });
      }

      listEmails.push({
        email: formInstance.recipient.email,
        formInstanceId: formInstance._id,
        assignBy: generateFullName(formInstance.assignedBy),
        pinCode: formInstance.pinCode.pinCode,
        formName: form.name,
        formDescription: form.description,
        organizationName: organization.name,
        scopedObjectLabel,
        assignedScopedObject: formInstance.scopedObjectLabel,
        otherRecipientIds: mappedRecipientToRecipientOwnerId[id],
        language: organization.primaryLanguage,
        organizationId: organization._id,
      });
    });

    resolve(sendEmail.assignFormToRecipients(listEmails));
  });
};

export const generateQueryFormByTypeOrPeriod = ({ formType, startDate, endDate }) => {
  let result = {};
  if (formType) result = { formType };
  if (startDate)
    result = { ...result, 'formPeriod.startDate': { $gte: new Date(moment.utc(startDate).startOf('day').format()) } };
  if (endDate)
    result = { ...result, 'formPeriod.endDate': { $lte: new Date(moment.utc(endDate).endOf('day').format()) } };

  return result;
};

