import axios from 'axios';
import _ from 'lodash';
import logger from '../../config/logger';
import { clientUrl, TE_APPS_EMAIL_ACCESS_TOKEN, TE_APPS_EMAIL_ENDPOINT } from '../../config/vars';
import { tePrefTranslate } from '../services/translation.service';

const CHUNK_SIZE = 100;

const EMAIL_TEMPLATE = {
  assignForm: 'te-prefs-form-assigned',
  resendPinCode: 'te-prefs-request-pincode-email',
  welcomeRecipient: 'te-pref-welcome-preference',
  viewAllForm: 'te-prefs-view-all-form',
  viewerLink: 'te-prefs-viewer-link-submission',
  acceptanceStatusChangedTemplate: 'te-prefs-acceptance-status-notification',
};

export default class Emails {
  constructor(region) {
    this.clientUrl = clientUrl;
    this.url = `${TE_APPS_EMAIL_ENDPOINT}/v1/email`;
    this.headers = {
      'Content-Type': 'application/json',
      Authorization: `Bearer ${TE_APPS_EMAIL_ACCESS_TOKEN}`,
    };
    this.form = 'TE Preferences<do-not-reply@timeedit.com>';
    this.region = region;
  }

  set(field, token) {
    this[field] = token;
  }

  toAssignFormEmail({
    to,
    formInstanceId,
    pinCode,
    formName,
    formDescription = '',
    organizationName,
    scopedObjectLabel,
    assignedScopedObject,
    language,
    organizationId,
  }) {
    return {
      to,
      from: this.form,
      subject: tePrefTranslate(language, 'mail_subject_requested_to_fill_form'),
      templateId: EMAIL_TEMPLATE.assignForm,
      dynamic_template_data: {
        recipient_name: to,
        form_name: formName,
        form_url: `${this.clientUrl}/form-instances/${formInstanceId}?pinCode=${pinCode}${
          this.region ? `&region=${this.region}` : ''
        }&organizationId=${organizationId}`,
        form_description: formDescription,
        organization_name: organizationName,
        scoped_object_label: scopedObjectLabel, // Form's Primary Object label
        assigned_scoped_object: assignedScopedObject, // FormInstance's scoped object label
        language,
      },
    };
  }

  async sendAssignFormEmail(
    {
      to,
      formInstanceId,
      pinCode,
      formName,
      formDescription = '',
      organizationName,
      scopedObjectLabel,
      assignedScopedObject,
      language,
      organizationId,
    },
    failOnError,
  ) {
    const msg = this.toAssignFormEmail({
      to,
      formInstanceId,
      pinCode,
      formName,
      formDescription,
      organizationName,
      scopedObjectLabel,
      assignedScopedObject,
      language,
      organizationId,
    });
    await this.send(msg, failOnError);
  }

  async assignFormToRecipients(data, failOnError) {
    const msgs = data.map((i) =>
      this.toAssignFormEmail({
        to: i.email,
        formInstanceId: i.formInstanceId,
        pinCode: i.pinCode,
        formName: i.formName,
        formDescription: i.formDescription,
        organizationName: i.organizationName,
        scopedObjectLabel: i.scopedObjectLabel,
        assignedScopedObject: i.assignedScopedObject,
        language: i.language,
        organizationId: i.organizationId,
      }),
    );
    await this.send(msgs, failOnError);
  }

  async resendPinCode(to, recipientName, pinCode, failOnError, language) {
    const msg = {
      to,
      from: this.form,
      subject: tePrefTranslate(language, 'mail_subject_pin_code'),
      templateId: EMAIL_TEMPLATE.resendPinCode,
      dynamic_template_data: {
        recipient_name: recipientName,
        pin_code: pinCode,
        language,
      },
    };

    await this.send(msg, failOnError);
  }

  async welcomeRecipient(
    {
      to,
      recipientName,
      viewFormsUrl,
      pinCode,
      formDescription = '',
      organizationName,
      scopedObjectLabel,
      language,
      organizationId,
    },
    failOnError,
  ) {
    const msg = {
      to,
      from: this.form,
      subject: tePrefTranslate(language, 'mail_subject_welcome_pref'),
      templateId: EMAIL_TEMPLATE.welcomeRecipient,
      dynamic_template_data: {
        recipient_name: recipientName,
        form_instance_url: `${this.clientUrl}/${viewFormsUrl}${this.region ? `&region=${this.region}` : ''}&organizationId=${organizationId}`,
        pin_code: pinCode,
        form_description: formDescription,
        organization_name: organizationName,
        scoped_object_label: scopedObjectLabel,
        language,
      },
    };

    await this.send(msg, failOnError);
  }

  async viewAllForm(
    { to, recipientName, viewFormsUrl, formName, formDescription = '', organizationName, scopedObjectLabel, language },
    failOnError,
  ) {
    const msg = {
      to,
      from: this.form,
      subject: tePrefTranslate(language, 'mail_subject__all_forms'),
      templateId: EMAIL_TEMPLATE.viewAllForm,
      dynamic_template_data: {
        recipient_name: recipientName,
        form_name: formName,
        all_form_url: `${this.clientUrl}/${viewFormsUrl}${this.region ? `&region=${this.region}` : ''}`,
        form_description: formDescription,
        organization_name: organizationName,
        scoped_object_label: scopedObjectLabel,
        language,
      },
    };

    await this.send(msg, failOnError);
  }

  async notifyViewerLink(payloads, failOnError) {
    const msgs = payloads.map((payload) => ({
      to: payload.to,
      from: this.form,
      subject: payload.subject,
      templateId: EMAIL_TEMPLATE.viewerLink,
      dynamic_template_data: payload.dynamicData,
    }));
    await this.send(msgs, failOnError);
  }

  async acceptanceStatusChangedMail(
    to,
    language,
    primaryObjectLabel,
    acceptanceStatus,
    acceptanceComment,
    formInstanceId,
    pinCode,
    failOnError,
    organizationId,
  ) {
    const acceptanceStatusTranslated = tePrefTranslate(language, `label_${acceptanceStatus.toLowerCase()}`);
    const msg = {
      to,
      from: this.form,
      subject: tePrefTranslate(language, 'mail_subject_acceptance_status_changed', {
        acceptanceStatus: acceptanceStatusTranslated,
      }),
      templateId: EMAIL_TEMPLATE.acceptanceStatusChangedTemplate,
      dynamic_template_data: {
        acceptanceStatus: acceptanceStatusTranslated,
        acceptanceComment,
        primaryObjectLabel,
        url: `${this.clientUrl}/form-instances/${formInstanceId}?pinCode=${pinCode}${
          this.region ? `&region=${this.region}` : ''
        }&organizationId=${organizationId}`,
        language,
      },
    };

    await this.send(msg, failOnError);
  }

  async doSending(msg, failOnError) {
    try {
      await axios({
        method: 'POST',
        url: this.url,
        headers: this.headers,
        data: msg,
      });
    } catch (error) {
      if (failOnError) {
        throw new Error(error);
      } else {
        logger.error(error);
      }
    }
  }

  async send(msg, failOnError) {
    const chunks = _.chunk(Array.isArray(msg) ? msg : [msg], CHUNK_SIZE);

    if (failOnError) {
      const promises = chunks.map((chunk) => this.doSending(chunk, failOnError));
      try {
        await Promise.all(promises);
      } catch (error) {
        throw new Error(error);
      }
    } else {
      let result = Promise.resolve();
      chunks.forEach((chunk) => {
        result = result.then(() => this.doSending(chunk, failOnError));
      });
    }
  }
}

