sourcesContent:
import merge from 'deepmerge';
import { IntlCache, IntlShape, createIntl, createIntlCache } from '@formatjs/intl';
import {
  tePreferencesSupportedLanguages,
  TypeTePreferencesSupportedLanguages,
} from '@timeedit/preferences-and-dm-commons/lib/src';

const langMessages: Record<
  TypeTePreferencesSupportedLanguages,
  Record<string, string>
> = tePreferencesSupportedLanguages.reduce(
  (total, current) => {
    return { ...total, [current]: require(`@timeedit/preferences-translations/translations/lang_${current}.json`) };
  },
  {} as Record<TypeTePreferencesSupportedLanguages, Record<string, string>>,
);

const caches = {} as Record<TypeTePreferencesSupportedLanguages, IntlCache>;
const intlObjectsPerLanguage = {} as Record<TypeTePreferencesSupportedLanguages, IntlShape>;

const makeSureEnglishIsUsedAsFallbackForMissingStringsInOtherLanguages = (inputLang: Record<string, string>) => {
  /* Unfortunately, POEditor, returns non-translated terms as empty strings. We need to remove them so that deep-merge works. */
  const inputLangCleaned: Record<string, string> = {};
  for (const key in inputLang) {
    if (inputLang[key]) {
      inputLangCleaned[key] = inputLang[key];
    }
  }
  return merge.all([langMessages.en, inputLangCleaned]) as Record<string, string>;
};

for (const lang of tePreferencesSupportedLanguages) {
  caches[lang] = createIntlCache();
  intlObjectsPerLanguage[lang] = createIntl(
    {
      locale: lang,
      messages: makeSureEnglishIsUsedAsFallbackForMissingStringsInOtherLanguages(langMessages[lang]),
    },
    caches[lang],
  );
}

export const tePrefTranslate = (
  language: TypeTePreferencesSupportedLanguages,
  translateString: string,
  translateStringParams?: Record<string, any>,
): string => {
  let intlObject = intlObjectsPerLanguage[language];
  if (!intlObject) {
    intlObject = intlObjectsPerLanguage.en;
  }

  return intlObject.formatMessage(
    {
      id: translateString,
    },
    translateStringParams,
  );
};

