sourcesContent:
import { Model, Schema } from 'mongoose';
import { getTenantModelEnh } from '../../utils/singleTenancyUtils';
import Plugin from './plugin';

const recipientGroupSchema = new Schema(
  {
    recipientIds: {
      type: [Schema.Types.ObjectId],
      default: [],
    },
    name: {
      type: String,
      required: true,
      trim: true,
    },
    description: {
      type: String,
      trim: true,
    },
    owner: {
      type: Schema.Types.ObjectId,
      required: true,
      index: true,
    },
    isActive: {
      type: Boolean,
      default: true,
    },
    assignedForms: [
      {
        _id: false,
        formId: {
          type: Schema.Types.ObjectId,
          required: true,
        },
        assignedBy: {
          type: Schema.Types.ObjectId,
          required: true,
        },
        assignedAt: {
          type: Date,
          default: new Date(),
        },
        scopedObjects: [
          {
            type: String,
          },
        ],
      },
    ],
  },
  {
    timestamps: true,
  },
);

recipientGroupSchema.plugin(Plugin);

export type TRecipientGroup = {
  recipientIds: string[];
  name: string;
  description: string;
  owner: string;
  isActive: boolean;
  assignedForms: {
    formId: string;
    assignedBy: string;
    assignedAt: Date;
    scopedObjects: string[];
  }[];
};

interface IRecipientGroupStatics {
  get(recipientId: string): unknown;
  list(query: string): unknown[];
  getRelatedFormInstances(recipientGroupId: string): unknown[];
  getListRecipientGroups(recipientGroupNames: string[]): unknown[];
}

interface IRecipientGroup extends Model<TRecipientGroup>, IRecipientGroupStatics {}

export const getRecipientGroupModel = async (organizationId: string) => {
  return getTenantModelEnh<TRecipientGroup, IRecipientGroup>(
    'recipientGroup',
    recipientGroupSchema,
    organizationId,
    (global as Record<string, unknown>).dbPrefix as string | undefined,
  );
};

