import { getTenantModel, createSession } from '@timeedit/be-utils/lib/common/dbConnection.common';
import { nodeCache } from '@timeedit/be-utils/lib/utils/nodeCache';
import { EDatabaseCluster } from '@timeedit/types/lib/enums/databaseCluster.enum';
import mongoose from 'mongoose';
import { getFormInstanceLookupModel } from '../models/tempSingleTenancyLookupModels/formInstanceLookup.model';
import { getFormLookupModel } from '../models/tempSingleTenancyLookupModels/formLookup.model';

const formCache: Record<string, string> = {};
const formInstanceCache: Record<string, string> = {};

function setConnectionInfoInV0(organizationId: string) {
  // Somewhat ugly workaround for v0: In v0, there is no organization service.
  // Hence, getTenantModel won't work. So, in order for getTenantModel skip fetching org at all,
  // we put the expected info in the cache before. Works only in v0. Don't try this at home!
  if (process.env.PLATFORM_VERSION !== 'v3') {
    nodeCache.set(`cluster-connection-${organizationId}`, {
      cluster: EDatabaseCluster.A,
      database: `tenant-${organizationId}`,
    });
  }
}

// Enh = Enhanced. See comment above at setConnectionInfoInV0 for the reason of this function.
export const getTenantModelEnh = async <T, U extends mongoose.Model<T> = mongoose.Model<T>>(
  name: string,
  schema: mongoose.Schema<T>,
  organizationId: string,
  prefix?: string,
) => {
  setConnectionInfoInV0(organizationId);
  return getTenantModel<T, U>(name, schema, organizationId, prefix);
};

// Enh = Enhanced. See comment above at setConnectionInfoInV0 for the reason of this function.
export const createSessionEnh = async (organizationId: string) => {
  setConnectionInfoInV0(organizationId);
  return createSession(organizationId);
};

/*
Temp Lookup functions for making smooth single-tenancy transition: We need to add organizationId to each query from FE.
But till that's done everywhere and everything works, we need to have lookups for organizationId for form and form instance.
*/

export const getOrgIdForForm = async (formId: string): Promise<string> => {
  const askCache = formCache[formId];
  if (askCache) {
    return askCache;
  }

  const FormLookupModel = await getFormLookupModel();
  const lookupFromDb = await FormLookupModel.findOne({ _id: formId });
  const organizationId = lookupFromDb?.organizationId.toString();

  if (!organizationId) {
    throw new Error(`No organization found in lookup for form ${formId}`);
  }

  formCache[formId] = organizationId;

  return organizationId;
};

export const getOrgIdForFormInstance = async (formInstanceId: string): Promise<string> => {
  const askCache = formInstanceCache[formInstanceId];
  if (askCache) {
    return askCache;
  }

  const FormInstanceLookupModel = await getFormInstanceLookupModel();
  const lookupFromDb = await FormInstanceLookupModel.findOne({ _id: formInstanceId });
  const organizationId = lookupFromDb?.organizationId.toString();

  if (!organizationId) {
    throw new Error(`No organization found in lookup for form instance ${formInstanceId}`);
  }

  formInstanceCache[formInstanceId] = organizationId;

  return organizationId;
};

