sourcesContent:
/* eslint no-param-reassign: "error" */
import httpStatus from 'http-status';
import mongoose from 'mongoose';
import { omitBy, isNil } from 'lodash';
import APIError from '../../utils/APIError';
import { DB_SERVICE_PREFIX } from '@timeedit/be-utils/lib/config/configBe';

async function get(this: any, recipientId) {
  try {
    let recipientGroup;

    if (mongoose.Types.ObjectId.isValid(recipientId)) {
      recipientGroup = await this.findOne({ _id: recipientId });
    }

    if (recipientGroup) {
      return recipientGroup;
    }

    throw new APIError({
      message: 'Recipient group does not exist',
      status: httpStatus.NOT_FOUND,
    });
  } catch (error) {
    throw new APIError({ message: error });
  }
}

/**
 * List notifications in descending order of 'createdAt' timestamp.
 *
 * @param {number} skip - Number of forms to be skipped.
 * @param {number} limit - Limit number of forms to be returned.
 * @returns {Promise<Notification[]>}
 */

async function list(this: any, query) {
  const options = omitBy(query, isNil);

  const recipientGroups = await this.find(options)
    .sort({ createdAt: -1 })
    // .skip(Number(perPage) * (page - 1))
    // .limit(Number(perPage))
    .exec();
  return recipientGroups;
}

async function getRelatedFormInstances(this: any, recipientGroupId) {
  try {
    const recipientGroup = await this.aggregate([
      { $match: { _id: new mongoose.Types.ObjectId(recipientGroupId) } },
      { $unwind: { path: '$assignedForms' } },
      {
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-forminstances`,
          let: { formId: '$assignedForms.formId', recipientGroupId: '$_id' },
          pipeline: [
            {
              $match: {
                $expr: {
                  $and: [{ $eq: ['$formId', '$$formId'] }, { $eq: ['$recipientGroupId', '$$recipientGroupId'] }],
                },
              },
            },
          ],
          as: 'assignedForms.formInstance',
        },
      },
      {
        $group: {
          _id: '$_id',
          recipientIds: {
            $first: '$recipientIds',
          },
          isActive: {
            $first: '$isActive',
          },
          name: {
            $first: '$name',
          },
          owner: {
            $first: '$owner',
          },
          assignedForms: {
            $push: '$assignedForms',
          },
        },
      },
    ]);
    return recipientGroup;
  } catch (error) {
    throw new APIError({ message: error });
  }
}

async function getListRecipientGroups(this: any, recipientGroupNames) {
  const recipientGroups = await this.find({
    name: { $in: recipientGroupNames },
  }).lean();

  return recipientGroups;
}

export default function (this: any, schema) {
  schema.statics = {
    ...this,
    list,
    get,
    getRelatedFormInstances,
    getListRecipientGroups,
  };
}

