import moment from 'moment';
import { Types } from 'mongoose';
import { TForm, TFormInstance, TRecipient } from '@timeedit/types/lib/types';

import { organizationId, branStarkUserId } from './dbUsers';
import { dbForms } from './dbForms';
import { formStatusTypes } from '../../../../lib/enums';
import { getFormInstanceModel } from '../../../../api/models/formInstance/formInstance.model';
import { getFormModel } from '../../../../api/models/forms/forms.model';
import { getPincodeModel } from '../../../../api/models/pincode/pincode.model';
import { getRecipientModel } from '../../../../api/models/recipients/recipients.model';

export const createAnotherFormInstance = async (
  organizationId: Types.ObjectId,
  formId: Types.ObjectId,
  recipientId: Types.ObjectId,
  formInstanceId?: Types.ObjectId,
  scopedObject?: string,
): Promise<TFormInstance> => {
  const FormInstanceModel = await getFormInstanceModel(organizationId.toString());
  const formInstance = await FormInstanceModel.create({
    _id: formInstanceId,
    formId,
    recipientId,
    scopedObject: scopedObject ?? '',
  });

  const RecipientModel = await getRecipientModel(organizationId.toString());
  const recipient = await RecipientModel.findOne({ _id: recipientId });

  recipient!.assignedForms = [
    ...recipient!.assignedForms,
    {
      formInstanceId: formInstance._id,
      formId,
      assignedBy: branStarkUserId,
      date: moment.utc().toDate(),
      scopedObject: formInstance.scopedObject,
    },
  ];

  recipient!.save();

  return formInstance;
};

export const createAnotherForm = async (objectScope?: string, options?: Partial<TForm>): Promise<TForm> => {
  const FormModel = await getFormModel(organizationId.toString());
  return FormModel.create({
    ...dbForms.Bran,
    organizationId,
    ownerId: branStarkUserId,
    objectScope,
    ...(options || {}),
  });
};

export const createAnotherRecipient = async (
  email: string,
  firstName?: string,
  lastName?: string,
): Promise<{ recipient: TRecipient; pinCode: string }> => {
  const RecipientModel = await getRecipientModel(organizationId.toString());
  const recipient = await RecipientModel.create(
    { isUser: false, email, firstName, lastName },
    organizationId.toString(),
  );
  const PincodeModel = await getPincodeModel(organizationId.toString());
  const pinCode = (await PincodeModel.find({ recipientId: recipient.id }).lean())[0].pinCode.toString();
  return { recipient, pinCode };
};

export const createFormInstanceRecipientAndPinCode = async (options?: { withSecondRecipient: boolean }) => {
  const FormModel = await getFormModel(organizationId.toString());
  await FormModel.deleteMany({});

  const RecipientModel = await getRecipientModel(organizationId.toString());
  await RecipientModel.deleteMany({});

  const FormInstanceModel = await getFormInstanceModel(organizationId.toString());
  await FormInstanceModel.deleteMany({});

  const PincodeModel = await getPincodeModel(organizationId.toString());
  await PincodeModel.deleteMany({});

  const bransFormId = (
    await FormModel.create({
      ...dbForms.Bran,
      organizationId,
      ownerId: branStarkUserId,
      status: formStatusTypes.FORM_STATUS_ACTIVE,
    })
  )._id;

  const recipient = await RecipientModel.create(
    { isUser: false, email: 'foo@timeedit.com' },
    organizationId.toString(),
  );
  const formInstanceId = (await FormInstanceModel.create({ formId: bransFormId, recipientId: recipient.id }))._id;

  recipient.assignedForms = [
    {
      formInstanceId,
      formId: bransFormId,
      assignedBy: branStarkUserId,
      date: moment.utc().toDate(),
      scopedObject: '',
    },
  ];

  await recipient.save();
  const pinCodeFind = await PincodeModel.find({ recipientId: recipient._id }).lean();

  let otherRecipientPinCodeFind;
  if (options?.withSecondRecipient) {
    const otherRecipient = await RecipientModel.create(
      { isUser: false, email: 'otherrecipient@timeedit.com' },
      organizationId.toString(),
    );
    otherRecipient.assignedForms = [
      {
        formInstanceId,
        formId: bransFormId,
        assignedBy: branStarkUserId,
        date: moment.utc().toDate(),
        scopedObject: '',
      },
    ];

    await FormInstanceModel.findOneAndUpdate({ _id: formInstanceId }, { otherRecipientIds: [otherRecipient.id] });

    await otherRecipient.save();
    otherRecipientPinCodeFind = await PincodeModel.find({ recipientId: otherRecipient.id }).lean();
  }

  return {
    bransFormId: bransFormId.toString(),
    recipientId: recipient.id.toString(),
    formInstanceId: formInstanceId.toString(),
    pinCodeString: pinCodeFind[0].pinCode.toString(),
    otherRecipientPinCodeFindString: otherRecipientPinCodeFind?.[0]?.pinCode.toString(),
  };
};

