/* eslint no-param-reassign: "error" */
import mongoose from 'mongoose';
import moment from 'moment';
import { URL } from 'url';
import httpStatus from 'http-status';
import { get as _get, groupBy, uniq, first, compact, isEmpty } from 'lodash';
import { FORM_INSTANCE_STATUS_TYPE } from '@timeedit/types/lib/constants';
import APIError from '../../utils/APIError';
import TEAdminService from '../../services/adminService';
import { mapAvailableViewerLink, AVAILABLE_VIEW_LINK } from '../../../lib/enums';
import { TE_VIEWER_ENDPOINT } from '../../../config/vars';
import { getFormModel } from '../forms/forms.model';
import logger from '../../../config/logger';
import { DB_SERVICE_PREFIX } from '@timeedit/be-utils/lib/config/configBe';
import { callActivityManagerToDeleteActivities } from '../../services/activityManager.service';

async function create(payload) {
  const formInstance = await new this({ ...payload });
  const newFormInstance = await formInstance.save();
  return newFormInstance;
}

async function get(formInstanceId) {
  const formInstance = await this.findOne({ _id: formInstanceId });
  if (!formInstance) {
    throw new APIError({
      status: httpStatus.NOT_FOUND,
      message: 'Form instance is not exist',
    });
  }
  return formInstance;
}

async function updateStatus(query, status) {
  try {
    const statusMapping = {
      sent: FORM_INSTANCE_STATUS_TYPE.SENT,
      opened: FORM_INSTANCE_STATUS_TYPE.OPENED,
      draft: FORM_INSTANCE_STATUS_TYPE.DRAFT,
      submitted: FORM_INSTANCE_STATUS_TYPE.SUBMITTED,
      overdue: FORM_INSTANCE_STATUS_TYPE.OVERDUE,
      invalidate: FORM_INSTANCE_STATUS_TYPE.INVALIDATE,
    };
    await this.findOneAndUpdate(query, {
      status: statusMapping[status],
    });
    return statusMapping[status];
  } catch (error) {
    throw new APIError({ message: error });
  }
}

const lookupRecipient = {
  $lookup: {
    from: `${DB_SERVICE_PREFIX}-recipients`,
    localField: 'recipientId',
    foreignField: '_id',
    as: 'recipients',
  },
};

const lookupOtherRecipients = {
  $lookup: {
    from: `${DB_SERVICE_PREFIX}-recipients`,
    let: {
      otherRecipientIds: '$otherRecipientIds',
    },
    pipeline: [
      {
        $match: {
          $expr: {
            $in: ['$_id', '$$otherRecipientIds'],
          },
        },
      },
      {
        $project: {
          _id: 1,
          email: 1,
          firstName: 1,
          lastName: 1,
        },
      },
    ],
    as: 'otherRecipients',
  },
};

const lookupOtherRecipientPinCodes = {
  $lookup: {
    let: {
      otherRecipientIds: '$otherRecipientIds',
    },
    from: `${DB_SERVICE_PREFIX}-pincodes`,
    pipeline: [
      {
        $match: {
          $expr: {
            $and: [
              {
                $ne: ['recipientId', null],
              },
              {
                $ne: ['email', null],
              },
              {
                $in: ['$recipientId', '$$otherRecipientIds'],
              },
            ],
          },
        },
      },
    ],
    as: 'otherRecipientPinCodes',
  },
};

const lookupRecipientGroup = {
  $lookup: {
    from: `${DB_SERVICE_PREFIX}-recipientgroups`,
    localField: 'recipientGroupId',
    foreignField: '_id',
    as: 'recipientGroups',
  },
};

const lookupPincode = {
  $lookup: {
    from: `${DB_SERVICE_PREFIX}-pincodes`,
    localField: 'recipientId',
    foreignField: 'recipientId',
    as: 'pinCodes',
  },
};

// TODO: Seems this does not exist -> check and remove!
const lookupElementValues = {
  $lookup: {
    from: `${DB_SERVICE_PREFIX}-elementvalues`,
    localField: '_id',
    foreignField: 'formInstanceId',
    as: 'values',
  },
};

//TODO: fix so we take timeZones into account instead of just adding 1 day
const formatDayWithTimezoneStartDate = (date) => {
  const utcMoment = moment.utc(date);
  const originalHour = utcMoment.get('hour');

  let formattedDate;

  if (originalHour >= 12 && originalHour < 24) {
    // For times between 12:00 and 23:59, add 1 day
    formattedDate = utcMoment.add(1, 'day');
  } else {
    // For other times, keep the day
    formattedDate = utcMoment;
  }

  return formattedDate.format('YYYYMMDD');
};

//TODO: fix so we take timeZones into account instead of just substract 1 day
const formatDayWithTimezoneEndDate = (date) => {
  const utcMoment = moment.utc(date);
  const originalHour = utcMoment.get('hour');

  let formattedDate;

  if (originalHour >= 0 && originalHour < 12) {
    // For times between 00:00 and 11:59, decrease 1 day
    formattedDate = utcMoment.subtract(1, 'day');
  } else {
    // For other times, keep the day
    formattedDate = utcMoment;
  }

  return formattedDate.format('YYYYMMDD');
};

export const generateReviewLink = (appSetting, formInstance, form) => {
  const viewerEntry = _get(appSetting, 'viewerEntry');
  const customerSignature = _get(appSetting, 'organization.customerSignature');
  const abilityGenerateLink = _get(appSetting, 'viewLinkSubmitter');

  // check if the organization setting allows to generate te viewer link
  // no available view link settings
  if (!mapAvailableViewerLink[abilityGenerateLink]) return undefined;

  // check if matching the condition setting
  if (!mapAvailableViewerLink[abilityGenerateLink].includes(_get(formInstance, 'teCoreProps.schedulingProgress', ''))) {
    return undefined;
  }

  if (abilityGenerateLink === AVAILABLE_VIEW_LINK.RECIPIENT_NOTIFIED && !formInstance.isNotifiedViewLink)
    return undefined;

  // check essential data
  if (!viewerEntry || !customerSignature) return undefined;

  // remove cloud_ on the start of the customerSignature as viewer do not want it
  const customerSignatureWithOutCloud = customerSignature.replace(/^cloud_/, '');

  let teViewerLink = `${TE_VIEWER_ENDPOINT}/${customerSignatureWithOutCloud}/web/${viewerEntry}`;

  if (form.formPeriod) {
    const transformFormPeriod = `${formatDayWithTimezoneStartDate(
      form.formPeriod.startDate,
    )}-${formatDayWithTimezoneEndDate(form.formPeriod.endDate)}`;
    const url = new URL(teViewerLink);
    if (url.search && url.search !== '') {
      teViewerLink += `&p=${transformFormPeriod}`;
    } else {
      teViewerLink += `?p=${transformFormPeriod}`;
    }
  }

  if (form.objectScope) {
    teViewerLink += `&type=${form.objectScope}`;
  }
  if (formInstance.scopedObject) {
    teViewerLink += `&object=${formInstance.scopedObject}`;
  }

  return teViewerLink;
};

const convertToMongooseObjectId = (str) => {
  if (str === 'true' || str === 'false') {
    return Boolean(str);
  }
  if (mongoose.isValidObjectId(str)) return new mongoose.Types.ObjectId(str);
  return str;
};
const convertQuery = (obj) =>
  Object.keys(obj).reduce(
    (results, key) => ({
      ...results,
      [key]: Array.isArray(obj[key])
        ? {
            $in: obj[key].map((q) => convertToMongooseObjectId(q)),
          }
        : convertToMongooseObjectId(obj[key]),
    }),
    {},
  );

const generateObjectQuery = ({ formId, formInstanceIds, freetext, ...restQuery }) => {
  const objectQuery = {
    $and: compact([
      {
        formId: new mongoose.Types.ObjectId(formId),
      },
      !isEmpty(restQuery) && convertQuery(restQuery),
    ]),
  };
  const generalSearchFields = [
    'firstName',
    'lastName',
    'email',
    'status',
    'scopedObjectLabel',
    'teCoreProps.acceptanceStatus, acceptanceComment',
    'recipientGroupName',
  ];
  if (freetext) {
    objectQuery.$and.push({
      $or: generalSearchFields.map((field) => ({
        [field]: {
          $regex: `${freetext}`,
          $options: 'i',
        },
      })),
    });
  }
  if (!isEmpty(formInstanceIds)) {
    objectQuery._id = {
      $in: formInstanceIds.map((id) => new mongoose.Types.ObjectId(id)),
    };
  }
  return objectQuery;
};

const generateAggregations = ({ objectQuery, order, orderBy }, { projects = {}, lookups = [] }) =>
  compact([
    lookupRecipient,
    ...lookups,
    {
      $project: {
        _id: 1,
        status: 1,
        formId: 1,
        createdAt: 1,
        updatedAt: 1,
        values: 1,
        scopedObject: 1,
        scopedObjectLabel: 1,
        submittedAt: 1,
        teCoreProps: 1,
        isCollaboration: 1,
        userId: { $arrayElemAt: ['$recipients.userId', 0] },
        recipientId: { $arrayElemAt: ['$recipients._id', 0] },
        email: { $arrayElemAt: ['$recipients.email', 0] },
        firstName: { $arrayElemAt: ['$recipients.firstName', 0] },
        lastName: { $arrayElemAt: ['$recipients.lastName', 0] },
        acceptanceComment: 1,
        ...projects,
      },
    },
    {
      $match: objectQuery,
    },
    order &&
      orderBy && {
        $sort: {
          [orderBy]: Number(order),
        },
      },
  ]);

async function findSubmissions(aggregations = [], { perPage = 10, page = 1, skip = 0 }) {
  try {
    const submissionsLookup = await this.aggregate([
      ...aggregations,
      {
        $facet: {
          submissions:
            perPage < 0 ? [] : [{ $skip: Number(perPage) * (page - 1) + Number(skip) }, { $limit: Number(perPage) }],
          total: [
            {
              $count: 'count',
            },
          ],
        },
      },
    ]).addFields({
      total: {
        $ifNull: [{ $arrayElemAt: ['$total.count', 0] }, 0],
      },
    });
    return submissionsLookup[0];
  } catch (e) {
    logger.log('Error >>>', e);
    return {
      submissions: [],
      total: 0,
    };
  }
}

async function getFormSubmission(
  {
    // page = 1,
    // perPage = 30,
    formInstanceId,
    organizationId,
  },
  accessToken,
) {
  try {
    const submissions = await this.aggregate([
      lookupRecipient,
      {
        $project: {
          _id: 1,
          status: 1,
          formId: 1,
          createdAt: 1,
          updatedAt: 1,
          userId: { $arrayElemAt: ['$recipients.userId', 0] },
          email: { $arrayElemAt: ['$recipients.email', 0] },
          firstName: { $arrayElemAt: ['$recipients.firstName', 0] },
          lastName: { $arrayElemAt: ['$recipients.lastName', 0] },
        },
      },
      {
        $match: {
          _id: new mongoose.Types.ObjectId(formInstanceId),
          status: FORM_INSTANCE_STATUS_TYPE.SUBMITTED,
        },
      },
      lookupElementValues,
      // { $skip: Number(perPage) * (page - 1) },
      // { $limit: Number(perPage) },
    ]);

    const userIds = submissions.map((recipient) => recipient.userId).filter((userId) => !!userId);
    const adminService = new TEAdminService(organizationId, accessToken);
    const users = await adminService.getUserByIds({
      userIds,
      fields: ['firstName', 'lastName'],
    });

    return _get(
      submissions.map((submission) => {
        const mapSubmission = { ...submission };
        const user = users.find((item) => submission.userId.equals(item._id));
        if (user) {
          mapSubmission.firstName = user.firstName;
          mapSubmission.lastName = user.lastName;
        }
        return mapSubmission;
      }),
      0,
      {},
    );
  } catch (error) {
    throw new APIError({ message: error });
  }
}

const relativeConstant = (displayName) => ({
  recipients: {
    referenceId: 'recipientId',
    relationship: {
      '1_1': { $arrayElemAt: [`$${displayName}`, 0] },
      '1_N': 1,
    },
  },
  forms: {
    referenceId: 'formId',
    relationship: {
      '1_1': { $arrayElemAt: [`$${displayName}`, 0] },
      '1_N': 1,
    },
  },
  recipientgroups: {
    referenceId: 'recipientGroupId',
    relationship: {
      '1_1': { $arrayElemAt: [`$${displayName}`, 0] },
      '1_N': 1,
    },
  },
});

async function getRelatedData(relatedData, FormInstanceModel) {
  try {
    const formInstanceId = this._id;
    const transformQuery = {
      lookups: [],
      fields: [],
    };
    relatedData.map((related) => {
      const tempConstant = relativeConstant(related.displayName);
      transformQuery.lookups.push({
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-${related.collection}`,
          localField: tempConstant[related.collection].referenceId,
          foreignField: '_id',
          as: related.displayName,
        },
      });

      transformQuery.fields[related.displayName] = tempConstant[related.collection].relationship[related.relationship];
      return related;
    });

    const formInstance = await FormInstanceModel.aggregate([
      { $match: { _id: new mongoose.Types.ObjectId(formInstanceId) } },
      ...transformQuery.lookups,
      {
        $project: {
          _id: 1,
          teCoreProps: 1,
          scopedObject: 1,
          scopedObjectLabel: 1,
          status: 1,
          formId: 1,
          recipientId: 1,
          recipientGroupId: 1,
          rollOverFormInstanceConfig: 1,
          values: 1,
          activityTemplateSettings: 1,
          groupManagementSettings: 1,
          jointTeachingFormConfig: 1,
          jointTeachingSettings: 1,
          otherRecipientIds: 1,
          isCollaboration: 1,
          isAllowToChangeScopedObject: 1,
          lockedAt: 1,
          lockedBy: 1,
          ...transformQuery.fields,
        },
      },
    ]);
    return formInstance[0];
  } catch (error) {
    throw new APIError({ message: error });
  }
}

async function listRelatedRecipientByFormId({ formId, organizationId }, accessToken) {
  let userIds = [];
  // get formInstance information
  const formInstances = (
    await this.aggregate([
      {
        $match: {
          formId: new mongoose.Types.ObjectId(formId),
          status: { $in: [FORM_INSTANCE_STATUS_TYPE.NOT_SENT] },
        },
      },
      {
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-recipients`,
          localField: 'recipientId',
          foreignField: '_id',
          as: 'recipient',
        },
      },
      { $unwind: { path: '$recipient' } },
      {
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-pincodes`,
          let: { recipientId: '$recipientId' },
          pipeline: [
            {
              $match: {
                $expr: {
                  $and: [{ $eq: ['$recipientId', '$$recipientId'] }],
                },
                // email: { $exists: false },
              },
            },
          ],
          as: 'pinCode',
        },
      },
      { $unwind: { path: '$pinCode' } },
      { $unwind: { path: '$recipient.assignedForms' } },
      { $match: { 'recipient.assignedForms.formId': new mongoose.Types.ObjectId(formId) } },
      {
        $group: {
          _id: '$_id',
          recipient: { $first: '$recipient' },
          pinCode: { $first: '$pinCode' },
        },
      },
    ])
  ).map((formInstance) => {
    const { assignedForms } = formInstance.recipient;
    if (formInstance._id.equals(assignedForms.formInstanceId)) {
      userIds.push(assignedForms.assignedBy.toString());
      formInstance.assignedBy = assignedForms.assignedBy;
    }
    return formInstance;
  });

  // get assigner information
  userIds = uniq(userIds);

  const adminService = new TEAdminService(organizationId, accessToken);
  const users = groupBy(
    await adminService.getUserByIds({
      userIds,
      fields: ['firstName', 'lastName'],
    }),
    '_id',
  );

  // map assigned by information to formInstance
  return formInstances.map((formInstance) => ({
    ...formInstance,
    assignedBy: first(users[formInstance.assignedBy]),
  }));
}

async function listRelatedRecipientByFormInstanceIds(
  { formInstanceIds, status = [], returnFields = [] },
  adminService,
) {
  let userIds = [];
  // get formInstance information
  const formInstances = (
    await this.aggregate([
      {
        $match: {
          _id: { $in: formInstanceIds },
          status: { $in: [...status, FORM_INSTANCE_STATUS_TYPE.NOT_SENT] },
        },
      },
      {
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-recipients`,
          localField: 'recipientId',
          foreignField: '_id',
          as: 'recipient',
        },
      },
      { $unwind: { path: '$recipient' } },
      {
        $lookup: {
          from: `${DB_SERVICE_PREFIX}-pincodes`,
          let: { recipientId: '$recipientId' },
          pipeline: [
            {
              $match: {
                $expr: {
                  $and: [{ $eq: ['$recipientId', '$$recipientId'] }],
                },
                // email: { $exists: false },
              },
            },
          ],
          as: 'pinCode',
        },
      },
      { $unwind: { path: '$pinCode' } },
      { $unwind: { path: '$recipient.assignedForms' } },
      { $match: { 'recipient.assignedForms.formInstanceId': { $in: formInstanceIds } } },
      {
        $group: {
          _id: '$_id',
          ...returnFields.reduce(
            (results, field) => ({
              ...results,
              [field]: { $first: `$${field}` },
            }),
            {},
          ),
          recipient: { $first: '$recipient' },
          pinCode: { $first: '$pinCode' },
        },
      },
    ])
  ).map((formInstance) => {
    const { assignedForms } = formInstance.recipient;
    if (formInstance._id.equals(assignedForms.formInstanceId)) {
      userIds.push(assignedForms.assignedBy.toString());
      formInstance.assignedBy = assignedForms.assignedBy;
    }
    return formInstance;
  });

  // get assigner information
  userIds = uniq(userIds);

  const users = groupBy(
    await adminService.getUserByIds({
      userIds,
      fields: ['firstName', 'lastName'],
    }),
    '_id',
  );

  // map assigned by information to formInstance
  return formInstances.map((formInstance) => ({
    ...formInstance,
    assignedBy: first(users[formInstance.assignedBy]),
  }));
}

async function listFormInstancesForViewLink({ formInstanceIds }) {
  const formInstances = await this.aggregate([
    {
      $match: {
        _id: {
          $in: formInstanceIds.map((formInstanceId) => new mongoose.Types.ObjectId(formInstanceId)),
        },
      },
    },
    lookupRecipient,
    { $unwind: '$recipients' },
  ]);
  return formInstances;
}

async function listRecipientInstances(
  { formId, page = 1, perPage = 10, skip = 0, order, orderBy, ...restQuery },
  accessToken,
  organizationId,
) {
  const objectQuery = generateObjectQuery({ formId, ...restQuery });
  const aggregations = generateAggregations(
    { objectQuery, order, orderBy },
    {
      projects: {
        pinCode: { $arrayElemAt: ['$pinCodes.pinCode', 0] },
        otherRecipientIds: 1,
        otherRecipients: 1,
        recipientGroupId: 1,
        recipientGroupName: { $arrayElemAt: ['$recipientGroups.name', 0] },
        otherRecipientPinCodes: 1,
      },
      lookups: [lookupPincode, lookupOtherRecipients, lookupOtherRecipientPinCodes, lookupRecipientGroup],
    },
  );
  const { submissions, total } = await this.findSubmissions(aggregations, { page, perPage, skip });
  const FormModel = await getFormModel(organizationId);
  const form = await FormModel.findOne({ _id: new mongoose.Types.ObjectId(formId) }).lean();
  const adminService = new TEAdminService(organizationId, accessToken);
  const appSetting = await adminService.getAppSettings();

  return {
    submissions: submissions.map((submission) => ({
      ...submission,
      numOfScheduledActivities: 0, // To be deleted once https://timeedit.atlassian.net/browse/CONST-432 is done. Left here for backwards compatibility
      reviewLink: generateReviewLink(appSetting, submission, form),
    })),
    total,
  };
}

async function preSave(next) {
  try {
    if (this.isModified && this.isModified('status')) {
      this.set({ previousStatus: this._previousStatus });
      if (this._previousStatus === FORM_INSTANCE_STATUS_TYPE.SUBMITTED) {
        await callActivityManagerToDeleteActivities(
          [this._id],
          (this.constructor?.schema || this.schema).statics.getOrganizationId(),
        );
      }
    }
  } catch (error) {
    logger.error(error);
  } finally {
    next();
  }
}

function getScopedObjectsData(baseMatch, matchForFormInstance = {}) {
  return this.aggregate([
    {
      $match: baseMatch,
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-forminstances`,
        pipeline: [
          {
            $match: {
              ...matchForFormInstance,
              status: { $ne: FORM_INSTANCE_STATUS_TYPE.INVALIDATE },
              scopedObject: {
                $ne: null,
              },
            },
          },
          {
            $lookup: {
              from: `${DB_SERVICE_PREFIX}-recipients`,
              localField: 'recipientId',
              foreignField: '_id',
              as: 'recipient',
            },
          },
          {
            $set: {
              recipient: { $arrayElemAt: ['$recipient', 0] },
            },
          },
          {
            $project: {
              _id: 0,
              id: '$scopedObject',
              status: 1,
              recipient: {
                firstName: '$recipient.firstName',
                lastName: '$recipient.lastName',
                email: '$recipient.email',
              },
              formInstanceId: '$_id',
            },
          },
        ],
        localField: 'formId',
        foreignField: 'formId',
        as: 'scopedObjects',
      },
    },
    {
      $project: {
        _id: 0,
        scopedObjects: 1,
      },
    },
    {
      $unwind: {
        path: '$scopedObjects',
      },
    },
    {
      $group: {
        _id: '$scopedObjects.id',
        data: {
          $addToSet: {
            formInstanceId: '$scopedObjects.formInstanceId',
            recipient: '$scopedObjects.recipient',
            status: '$scopedObjects.status',
          },
        },
      },
    },
    {
      $project: {
        _id: 0,
        id: '$_id',
        data: 1,
      },
    },
  ]);
}

async function listWithNumberOfStudyCombinationsAndRecipientDetails({ recipientId, ...restQuery }) {
  const aggregations = [
    {
      $match: {
        $and: [
          {
            $or: [
              { recipientId: new mongoose.Types.ObjectId(recipientId) },
              { otherRecipientIds: new mongoose.Types.ObjectId(recipientId) },
            ],
          },
          {
            ...restQuery,
          },
        ],
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-pathways`,
        let: { formInstanceId: '$_id' },
        pipeline: [
          {
            $match: {
              $expr: {
                $eq: ['$formInstanceId', '$$formInstanceId'],
              },
            },
          },
          { $project: { _id: 1 } },
        ],
        as: 'pathways',
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-recipients`,
        let: { recipientId: '$recipientId' },
        pipeline: [
          {
            $match: {
              $expr: {
                $and: [{ $eq: ['$_id', '$$recipientId'] }],
              },
            },
          },
          { $project: { email: 1, firstName: 1, lastName: 1 } },
        ],
        as: 'recipientDetail',
      },
    },
    {
      $addFields: {
        noOfPathways: { $size: '$pathways' },
        recipientDetail: { $arrayElemAt: ['$recipientDetail', 0] },
      },
    },
    {
      $project: {
        pathways: 0,
      },
    },
  ];

  return this.aggregate(aggregations);
}

async function listWithPagination(
  { form, page = 1, perPage = 10, showInvalid = 0, sort, order, search },
  accessToken,
  organizationId,
) {
  const skip = perPage * (page - 1);

  const adminService = new TEAdminService(organizationId, accessToken);

  const appSetting = await adminService.getAppSettings();
  let condition = {};

  condition = {
    ...condition,
    $expr: {
      $and: [
        { $eq: ['$formId', new mongoose.Types.ObjectId(form._id)] },
        { $ne: ['$status', FORM_INSTANCE_STATUS_TYPE.COLLABORATED] },
      ],
    },
  };

  if (showInvalid === 0) {
    condition.$expr.$and.push({ $ne: ['$status', FORM_INSTANCE_STATUS_TYPE.INVALIDATE] });
  } else {
    condition.$expr.$and.push({ $eq: ['$status', FORM_INSTANCE_STATUS_TYPE.INVALIDATE] });
  }

  const sorter = {};
  sorter[sort] = order;
  const firstStep = [
    {
      $match: condition,
    },
    { $addFields: { allRecipients: { $concatArrays: ['$otherRecipientIds', ['$recipientId']] } } },
    { $project: { values: 0 } },
    { $unwind: { path: '$allRecipients' } },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-recipients`,
        let: {
          otherRecipientIds: '$otherRecipientIds',
          recipientId: '$allRecipients',
        },
        pipeline: [
          {
            $match: {
              $expr: {
                $eq: ['$_id', '$$recipientId'],
              },
            },
          },
          {
            $project: {
              _id: 1,
              email: 1,
              firstName: 1,
              lastName: 1,
            },
          },
        ],
        as: 'recipients',
      },
    },
    { $project: { allRecipients: 0 } },
    { $unwind: { path: '$recipients' } },
    lookupRecipientGroup,
    { $unwind: { path: '$recipientGroups', preserveNullAndEmptyArrays: true } },
  ];
  const lastStep = [
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-pincodes`,
        let: {
          rId: '$recipients._id',
        },
        pipeline: [{ $match: { $expr: { $eq: ['$recipientId', '$$rId'] } } }],
        as: 'pinCodes',
      },
    },
    { $sort: sorter },
    { $skip: skip },
    { $limit: perPage },
  ];
  const pipeLines = [...firstStep];
  if (search) {
    pipeLines.push({
      $match: {
        $or: [
          { scopedObject: { $regex: new RegExp(search), $options: 'i' } },
          { 'recipients.email': { $regex: new RegExp(search), $options: 'i' } },
          { 'recipients.firstName': { $regex: new RegExp(search), $options: 'i' } },
          { 'recipientGroups.name': { $regex: new RegExp(search), $options: 'i' } },
        ],
      },
    });
  }
  const pipeLinesCount = [...pipeLines];
  pipeLinesCount.push({
    $count: 'total',
  });
  pipeLines.push(...lastStep);
  const result = await Promise.all([this.aggregate(pipeLinesCount), this.aggregate(pipeLines)]);

  return {
    data: result[1].map((f) => ({ ...f, reviewLink: generateReviewLink(appSetting, f, form) })),
    total: result[0].length > 0 ? result[0][0].total : 0,
    page,
    perPage,
  };
}

export const returnFormInstancePlugins = (organizationId) => {
  return (schema) => {
    schema.statics = {
      ...this,
      get,
      create,
      updateStatus,
      getFormSubmission,
      getRelatedData,
      listRelatedRecipientByFormId,
      listFormInstancesForViewLink,
      listRelatedRecipientByFormInstanceIds,
      listRecipientInstances,
      findSubmissions,
      listWithNumberOfStudyCombinationsAndRecipientDetails,
      listWithPagination,
      getScopedObjectsData,
      getOrganizationId: () => organizationId,
    };
    schema.method({
      ...this,
      getRelatedData,
    });
    schema.pre(/^(updateOne|save)/, preSave);
  };
};

