import axios from 'axios';
import { Types } from 'mongoose';
import { generateAuthTokenByRegion } from './adminService.js';

export const callActivityManagerToDeleteActivities = async (
  formInstanceIds: Array<Types.ObjectId>,
  organizationId: string,
) => {
  await axios.request({
    method: 'POST',
    url: `${process.env.ACTIVITY_MANAGER_URL}/v1/organization/${organizationId}/activities/delete-activities-by-forminstanceids`,
    headers: {
      Authorization: `Bearer ${generateAuthTokenByRegion(process.env.TE_REGION)}`,
    },
    data: { formInstanceIds: formInstanceIds.map((id) => id.toString()) },
  });
};

