import { Model, Schema, Types } from 'mongoose';
import pinCodePlugin from './plugin';
import { pinCodeTypes } from '../../../lib/enums';
import { getTenantModelEnh } from '../../utils/singleTenancyUtils';

export type TPinCode = {
  pinCode: string;
  userId: Types.ObjectId;
  email: string;
  formId: Types.ObjectId;
  recipientId: Types.ObjectId;
  type: keyof typeof pinCodeTypes;
  expires: Date;
};

/**
 * Refresh Token Schema
 * @private
 */
const pinCodeSchema = new Schema(
  {
    pinCode: {
      type: String,
      required: true,
      index: true,
      unique: true,
    },
    userId: {
      type: Schema.Types.ObjectId,
      ref: 'users',
      index: true,
    },
    email: {
      type: String,
      ref: 'users',
    },
    formId: {
      type: Schema.Types.ObjectId,
      ref: 'users',
      index: true,
    },
    recipientId: {
      type: Schema.Types.ObjectId,
      ref: 'users',
      index: true,
    },
    type: {
      type: String,
      enum: Object.keys(pinCodeTypes).map((type) => pinCodeTypes[type]),
      required: true,
    },
    expires: { type: Date },
  },
  {
    timestamps: true,
  },
);

pinCodeSchema.plugin(pinCodePlugin);

export const getPincodeModel = async (organizationId: string) => {
  return getTenantModelEnh<TPinCode, Model<TPinCode>>(
    'pinCode',
    pinCodeSchema,
    organizationId,
    (global as Record<string, unknown>).dbPrefix as string | undefined,
  );
};

