import { nanoid } from 'nanoid';
import { pinCodeTypes } from '../../../lib/enums';

/**
 * Generate a pin code and saves it into the database
 */

const generate = () => nanoid(14);

async function baseGenerate(option) {
  await this.findOneAndDelete({ ...option });
  const pinCode = await this.create({
    pinCode: generate(),
    ...option,
  });
  return pinCode;
}

async function inviteUser(email) {
  const pinCode = await this.baseGenerate({
    email,
    type: pinCodeTypes.REGISTER_ACCOUNT,
  });
  return pinCode;
}

async function findRecipientPinCode(recipientId) {
  const pinCode = await this.findOne({
    recipientId,
    type: pinCodeTypes.RECIPIENT_ACCESS,
  });
  return pinCode;
}

async function generatePinCodeForRecipient(recipientId) {
  const pinCode = await this.baseGenerate({
    recipientId,
    type: pinCodeTypes.RECIPIENT_ACCESS,
  });
  return pinCode;
}
async function generatePinCodeForRecipientAndForm(email, formId) {
  const pinCode = await this.baseGenerate({
    email,
    formId,
    type: pinCodeTypes.ASSIGN_FORM_EMAIL,
  });
  return pinCode;
}

async function validatePinCodeForRecipientFormInstances(email, formIds, pinCode) {
  const matchingPinCode = await this.find({ email, formId: { $in: formIds }, pinCode });
  if (!matchingPinCode || matchingPinCode.length === 0) return false;
  return true;
}

async function generatePinCodeForNewRecipients(options) {
  const pinCode = await this.baseGenerate({
    ...options,
    type: pinCodeTypes.RECIPIENT_ACCESS,
  });
  return pinCode;
}

export default function (schema) {
  schema.statics = {
    ...this,
    generate,
    baseGenerate,
    inviteUser,
    findRecipientPinCode,
    generatePinCodeForRecipient,
    generatePinCodeForRecipientAndForm,
    validatePinCodeForRecipientFormInstances,
    generatePinCodeForNewRecipients,
  };
}

