import { TRecipient } from '@timeedit/types/lib/types';
import recipientPlugins from './plugin';
import { getTenantModelEnh } from '../../utils/singleTenancyUtils';
import { HydratedDocument, Model } from 'mongoose';

RecipientSchema.plugin(recipientPlugins);

interface RecipientModelWithCreateFunction {
  create(payload: Partial<TRecipient>, organizationId: string): Promise<HydratedDocument<TRecipient>>;
  listByIdsSimple(
    recipientIds: string[] | null,
    additionalFields?: [],
  ): { _id: string; email: string; firstName: string; lastName: string }[];
}

type ITRecipientModel = Model<TRecipient> & RecipientModelWithCreateFunction;

export const getRecipientModel = async (organizationId: string) => {
  return getTenantModelEnh<TRecipient, ITRecipientModel>(
    'recipients',
    RecipientSchema,
    organizationId,
    (global as Record<string, unknown>).dbPrefix as string | undefined,
  );
};

