/* eslint no-param-reassign: "error" */
import mongoose from 'mongoose';
import httpStatus from 'http-status';
import { remove, isNil } from 'lodash';
import APIError from '../../utils/APIError';
import { queryEmailOrUserId } from '../../utils/helpers';
import { formInstanceStatusTypes, formStatusTypes } from '../../../lib/enums';
import TEAdminService from '../../services/adminService';
import { getPincodeModel } from '../pincode/pincode.model';
import { DB_SERVICE_PREFIX } from '@timeedit/be-utils/lib/config/configBe';

const lookupForms = {
  $lookup: {
    from: `${DB_SERVICE_PREFIX}-forms`,
    localField: 'assignedForms.formId',
    foreignField: '_id',
    as: 'forms',
  },
};

const lookupFormInstances = {
  $lookup: {
    from: `${DB_SERVICE_PREFIX}-forminstances`,
    localField: 'assignedForms.formInstanceId',
    foreignField: '_id',
    as: 'formInstances',
  },
};

const lookupElementValues = {
  $lookup: {
    from: `${DB_SERVICE_PREFIX}-elementvalues`,
    localField: 'formInstances._id',
    foreignField: 'formInstanceId',
    as: 'formInstance.values',
  },
};

const lookupPinCode = {
  $lookup: {
    from: `${DB_SERVICE_PREFIX}-pincodes`,
    localField: '_id',
    foreignField: 'recipientId',
    as: 'pinCodes',
  },
};

const unwindAssignedForm = { $unwind: { path: '$assignedForms' } };

const unwindFormInstances = { $unwind: { path: '$formInstances' } };

const matchUserOrEmail = (userId, email) => ({
  $match: {
    ...queryEmailOrUserId({ userId, email }),
  },
});

const matchFormId = (formId) => ({
  $match: {
    'assignedForms.formId': new mongoose.Types.ObjectId(formId),
  },
});

async function create(payload, organizationId) {
  const recipientObject = await new this({ ...payload });
  const newRecipient = await recipientObject.save();
  // Create pinCode for recipient
  // TODO: Need to check if new recipient already has pinCode?
  const PincodeModel = await getPincodeModel(organizationId);
  await PincodeModel.generatePinCodeForRecipient(newRecipient._id);
  return newRecipient;
}

async function listByUserIdAndEmail({ userId, email }, organizationId, accessToken) {
  // query all recipients related

  const recipients = await this.aggregate([
    matchUserOrEmail(new mongoose.Types.ObjectId(userId), email),
    unwindAssignedForm,
    lookupForms,
    {
      $project: {
        _id: 1,
        email: 1,
        userId: 1,
        assignedForms: 1,
        form: { $arrayElemAt: ['$forms', 0] },
      },
    },
    {
      $match: { 'form.status': { $ne: formStatusTypes.FORM_STATUS_ARCHIVED } },
    },
    lookupFormInstances,
    unwindFormInstances,
    lookupElementValues,
    {
      $group: {
        _id: '$_id',
        email: { $first: '$email' },
        userId: { $first: '$userId' },
        forms: { $push: '$form' },
        formInstances: { $push: '$formInstances' },
      },
    },
  ]);

  // get userIds to query user information from te admin
  const userIds = (
    await this.aggregate([
      matchUserOrEmail(new mongoose.Types.ObjectId(userId), email),
      unwindAssignedForm,
      lookupForms,
      {
        $project: {
          _id: 1,
          email: 1,
          userId: 1,
          assignedForms: 1,
          form: { $arrayElemAt: ['$forms', 0] },
        },
      },
      {
        $match: { 'form.status': { $ne: formStatusTypes.FORM_STATUS_ARCHIVED } },
      },
      {
        $group: {
          _id: '$form.ownerId',
        },
      },
    ])
  ).map(({ _id }) => _id);
  remove(userIds, isNil);
  const adminService = new TEAdminService(organizationId, accessToken);
  const owners = await adminService.getUserByIds({
    userIds,
    fields: ['firstName', 'lastName'],
  });
  return { recipients, owners };
}

async function listByFormId(formId) {
  const customFormInstanceFields = {
    activityTemplateSettings: { operator: '$first' },
    groupManagementSettings: { operator: '$first' },
    jointTeachingFormConfig: { operator: '$first' },
    otherRecipientIds: { operator: '$push' },
    otherRecipients: { operator: '$push' },
    otherRecipientPincodes: { operator: '$push' },
    scopedObject: { operator: '$first' },
    scopedObjectLabel: { operator: '$first' },
    isCollaboration: { operator: '$first' },
    status: { operator: '$first' },
    isPublic: { operator: '$first' },
    isNotifiedViewLink: { operator: '$first' },
    rollOverFormInstanceConfig: { operator: '$first' },
    formId: { operator: '$first' },
    recipientId: { operator: '$first' },
    recipientGroupId: { operator: '$first' },
    teCoreProps: { operator: '$first' },
  };

  const recipients = await this.aggregate([
    // The condition is redudant (logic-wise), but putting such a $match stage before $lookup makes everything so much faster!
    {
      $match: {
        'assignedForms.formId': new mongoose.Types.ObjectId(formId),
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-forminstances`,
        let: { recipientId: '$_id', formId: new mongoose.Types.ObjectId(formId) },
        pipeline: [
          {
            $match: {
              $expr: {
                $and: [
                  { $eq: ['$$formId', '$formId'] },
                  { $eq: ['$$recipientId', '$recipientId'] },
                  { $ne: ['$status', formInstanceStatusTypes.COLLABORATED] },
                ],
              },
            },
          },
          {
            $project: {
              _id: 1,
              ...Object.keys(customFormInstanceFields).reduce(
                (results, key) => ({
                  ...results,
                  [key]: 1,
                }),
                {},
              ),
            },
          },
        ],
        as: 'formInstances',
      },
    },
    { $unwind: { path: '$formInstances' } },
    lookupPinCode,
    {
      $unwind: {
        path: '$formInstances.otherRecipientIds',
        preserveNullAndEmptyArrays: true,
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-pincodes`,
        let: { otherRecipientIds: '$formInstances.otherRecipientIds' },
        pipeline: [
          {
            $match: {
              $expr: {
                $and: [
                  { $eq: ['$recipientId', '$$otherRecipientIds'] },
                  {
                    $or: [
                      {
                        $and: [
                          { $ne: [{ $type: '$$otherRecipientIds' }, 'missing'] },
                          { $ne: [{ $type: '$$otherRecipientIds' }, null] },
                          { $ne: [{ $type: '$$otherRecipientIds' }, []] },
                        ],
                      },
                    ],
                  },
                ],
              },
            },
          },
        ],
        as: 'formInstances.otherRecipientPincodes',
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-recipients`,
        let: {
          otherRecipientIds: '$formInstances.otherRecipientIds',
        },
        pipeline: [
          {
            $match: {
              $expr: {
                $and: [
                  { $eq: ['$_id', '$$otherRecipientIds'] },
                  { $ne: [{ $type: '$$otherRecipientIds' }, 'missing'] },
                  { $ne: [{ $type: '$$otherRecipientIds' }, null] },
                  { $ne: [{ $type: '$$otherRecipientIds' }, []] },
                ],
              },
            },
          },
          {
            $project: {
              assignedForms: 0,
            },
          },
        ],
        as: 'formInstances.otherRecipients',
      },
    },
    {
      $project: {
        _id: 1,
        email: 1,
        firstName: 1,
        lastName: 1,
        pinCode: { $arrayElemAt: ['$pinCodes', 0] },
        formInstances: {
          _id: 1,
          ...Object.keys(customFormInstanceFields).reduce(
            (response, item) => ({
              ...response,
              [item]: 1,
            }),
            {},
          ),
          otherRecipients: { $arrayElemAt: ['$formInstances.otherRecipients', 0] },
          otherRecipientPincodes: { $arrayElemAt: ['$formInstances.otherRecipientPincodes', 0] },
        },
      },
    },
    {
      $group: {
        _id: '$formInstances._id',
        originRecipientId: { $first: '$_id' },
        email: { $first: '$email' },
        firstName: { $first: '$firstName' },
        lastName: { $first: '$lastName' },
        pinCode: { $first: '$pinCode' },
        assignedForms: { $first: '$assignedForms' },
        formInstanceId: { $first: '$formInstances._id' },
        ...Object.keys(customFormInstanceFields).reduce(
          (response, item) => ({
            ...response,
            [item]: { [customFormInstanceFields[item].operator]: `$formInstances.${item}` },
          }),
          {},
        ),
      },
    },
    {
      $group: {
        _id: '$originRecipientId',
        email: { $first: '$email' },
        firstName: { $first: '$firstName' },
        lastName: { $first: '$lastName' },
        pinCode: { $first: '$pinCode' },
        assignedForms: { $first: '$assignedForms' },
        formInstances: {
          $push: {
            _id: '$formInstanceId',
            ...Object.keys(customFormInstanceFields)
              .filter((key) => key !== 'values') // Remove values from response
              .reduce(
                (response, item) => ({
                  ...response,
                  [item]: `$${item}`,
                }),
                {},
              ),
          },
        },
      },
    },
  ]).allowDiskUse(true);
  return recipients;
}

async function get({ formId, userId, email }) {
  const recipient = await this.findOne({
    ...queryEmailOrUserId({ userId, email }),
    'assignedForms.formId': formId,
  }).exec();

  if (!recipient) {
    const error = new APIError({
      message: 'You do not have permission',
      status: httpStatus.FORBIDDEN,
    });
    throw error;
  }
  return recipient;
}

async function getAllRelatedData({ formId, userId, email }) {
  const recipients = await this.aggregate([
    unwindAssignedForm,
    matchFormId(formId),
    matchUserOrEmail(userId, email),
    lookupForms,
    lookupFormInstances,
    {
      $project: {
        isUser: 1,
        email: 1,
        userId: 1,
        firstName: 1,
        lastName: 1,
        form: { $arrayElemAt: ['$forms', 0] },
        formInstance: { $arrayElemAt: ['$formInstances', 0] },
      },
    },
    lookupElementValues,
  ]);
  return recipients;
}

async function getFormsAndFormInstancesById(recipientId) {
  const queryAggregate = [
    {
      $match: {
        _id: new mongoose.Types.ObjectId(recipientId),
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-forminstances`,
        let: { recipientId: '$_id' },
        pipeline: [
          {
            $match: {
              $expr: {
                $and: [
                  { $ne: [formInstanceStatusTypes.COLLABORATED, '$status'] },
                  {
                    $or: [{ $eq: ['$recipientId', '$$recipientId'] }, { $in: ['$$recipientId', '$otherRecipientIds'] }],
                  },
                ],
              },
            },
          },
          {
            $project: {
              formId: 1,
              _id: 1,
            },
          },
        ],
        as: 'formInstances',
      },
    },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-forms`,
        let: { allFormIdsVariable: '$formInstances.formId' },
        pipeline: [
          {
            $match: {
              $expr: {
                $and: [
                  { $in: ['$_id', '$$allFormIdsVariable'] },
                  { $in: ['$status', [formStatusTypes.FORM_STATUS_ACTIVE]] },
                ],
              },
            },
          },
        ],
        as: 'forms',
      },
    },
    {
      $unwind: {
        path: '$forms',
      },
    },
  ];

  const result = await this.aggregate(queryAggregate);

  return result;
}

async function listByIds(recipientIds) {
  const recipients = await this.aggregate([
    { $match: { _id: { $in: recipientIds } } },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-pincodes`,
        localField: '_id',
        foreignField: 'recipientId',
        as: 'pincode',
      },
    },
    { $unwind: { path: '$pincode' } },
    {
      $group: {
        _id: '$_id',
        email: { $first: '$email' },
        assignedForms: { $first: '$assignedForms' },
        firstName: { $first: '$firstName' },
        lastName: { $first: '$lastName' },
        pinCode: { $first: '$pincode.pinCode' },
      },
    },
  ]);
  return recipients;
}

async function listByIdsSimple(recipientIds, additionalFields) {
  const conditition = recipientIds ? { _id: { $in: recipientIds } } : {};
  const recipients = await this.find(conditition)
    .select(`_id firstName lastName email ${additionalFields ?? [].join(' ')}`)
    .sort({ email: 1 })
    .lean();
  return recipients;
}

async function findRecipientEmailsWithPincode(emails) {
  const recipients = await this.aggregate([
    { $match: { email: { $in: emails } } },
    {
      $lookup: {
        from: `${DB_SERVICE_PREFIX}-pincodes`,
        localField: '_id',
        foreignField: 'recipientId',
        as: 'pincode',
      },
    },
    { $unwind: { path: '$pincode' } },
    {
      $group: {
        _id: '$_id',
        email: { $first: '$email' },
        assignedForms: { $first: '$assignedForms' },
        firstName: { $first: '$firstName' },
        lastName: { $first: '$lastName' },
        pinCode: { $first: '$pincode.pinCode' },
      },
    },
  ]);
  return recipients;
}

export default function (schema) {
  schema.statics = {
    ...this,
    create,
    listByUserIdAndEmail,
    getAllRelatedData,
    listByFormId,
    get,
    getFormsAndFormInstancesById,
    listByIds,
    listByIdsSimple,
    findRecipientEmailsWithPincode,
  };
}

