import { getTenantModel } from '@timeedit/be-utils/lib/common/dbConnection.common';
import mongoose from 'mongoose';

const ChildSchema = new mongoose.Schema({
  anotherProp: { type: String },
});

const MotherSchema = new mongoose.Schema({
  someProp: { type: String },
  myChildren: {
    type: [ChildSchema],
  },
});

export const getFormModel = async (organizationId: string) => {
  return getTenantModel(
    'forms',
    MotherSchema,
    organizationId,
    (global as Record<string, unknown>).dbPrefix as string | undefined,
  );
};

