sourcesContent:
import { FormInstanceSchema } from '@timeedit/te-data-models/schema';
import { TFormInstance, TRecipient } from '@timeedit/types/lib/types';
import { FORM_INSTANCE_STATUS_TYPE } from '@timeedit/types/lib/constants';
import { Model, Types } from 'mongoose';
import { returnFormInstancePlugins } from './plugin';
import { TPinCode } from '../pincode/pincode.model';
import { getTenantModelEnh } from '../../utils/singleTenancyUtils';
import { cloneDeep } from 'lodash';

type TFormInstanceWithRecipient = TFormInstance & {
  assignedBy: {
    firstName: string;
    lastName: string;
  };
  pinCode: TPinCode;
  recipient: TRecipient;
};

type TScopedObjectData = {
  id: Types.ObjectId;
  data: {
    status: keyof typeof FORM_INSTANCE_STATUS_TYPE;
    recipient: {
      firstName: string;
      lastName: string;
    };
    scopedObject: string;
  };
};

interface IFormInstanceStatics {
  listRelatedRecipientByFormId(
    args: {
      formId: Types.ObjectId | string;
      organizationId: string;
    },
    token,
  ): TFormInstanceWithRecipient[];
  listRelatedRecipientByFormInstanceIds(
    args: {
      formInstanceIds: (Types.ObjectId | string)[];
      status?: (keyof typeof FORM_INSTANCE_STATUS_TYPE)[];
      returnFields?: unknown;
    },
    adminService: unknown,
  ): TFormInstanceWithRecipient[];
  getScopedObjectsData(baseMatch: object, matchForFormInstance: object): TScopedObjectData[];
}

interface IFormInstanceModel extends Model<TFormInstance>, IFormInstanceStatics {}

export const getFormInstanceModel = async (organizationId: string) => {
  const FormInstanceSchemaCopy = cloneDeep(FormInstanceSchema);
  FormInstanceSchemaCopy.plugin(returnFormInstancePlugins(organizationId));
  return getTenantModelEnh<TFormInstance, IFormInstanceModel>(
    'formInstances',
    FormInstanceSchemaCopy,
    organizationId,
    (global as Record<string, unknown>).dbPrefix as string | undefined,
  );
};

export const smallerInstanceFields: Partial<keyof TFormInstance>[] = [
  '_id',
  'recipientId',
  'isCollaboration',
  'otherRecipientIds',
  'formId',
  'status',
  'collaborationId',
  'lockedBy',
  'lockedAt',
  'selectedTeachingPeriod',
  'scopedObject',
  'scopedObjectLabel',
  'teCoreProps',
  'isNotifiedViewLink',
];
// Those fields should usually not be that big. Load those preferably and not all fields.

