sourcesContent:
import httpStatus from 'http-status';

/**
 * @extends Error
 */

type TErrorParams = {
  message: any;
  status?: number;
  errors?: any;
  isPublic?: boolean;
  isOperational?: boolean;
  stack?: any;
  logAs?: string; // Log levels
};

class ExtendableError extends Error {
  errors: any;
  status: number;
  isPublic: boolean;
  isOperational: boolean;
  stack: any;
  logAs: string; // Log levels

  constructor({ message, errors, status, isPublic, stack, logAs }: TErrorParams) {
    super(message);
    this.name = this.constructor.name;
    this.message = message;
    this.errors = errors;
    this.status = status ?? httpStatus.INTERNAL_SERVER_ERROR;
    this.isPublic = isPublic ?? false;
    this.isOperational = true; // This is required since bluebird 4 doesn't append it anymore.
    this.stack = stack;
    this.logAs = logAs || 'error';
    // Error.captureStackTrace(this, this.constructor.name);
  }
}

/**
 * Class representing an API error.
 * @extends ExtendableError
 */
class APIError extends ExtendableError {
  /**
   * Creates an API error.
   * @param {string} message - Error message.
   * @param {number} status - HTTP status code of error.
   * @param {boolean} isPublic - Whether the message should be visible to user or not.
   * @param {string} logAs - At which level it should be logged. Standard Google log levels. Sometimes, you want errors just to be logged as warnings. In that case, set warn
   */
  constructor({
    message,
    status = httpStatus.INTERNAL_SERVER_ERROR,
    errors = undefined,
    stack = undefined,
    isPublic = false,
    logAs = 'error',
  }: TErrorParams) {
    super({
      message,
      errors,
      status,
      isPublic,
      stack,
      logAs,
    });
  }
}

export default APIError;

