sourcesContent:
import winston from 'winston';
import { LoggingWinston } from '@google-cloud/logging-winston';
import compact from 'lodash/compact';
import vars from './vars';

const googleCredential = ['test', 'development'].includes(vars.env)
  ? undefined
  : {
      projectId: vars.PROJECT_ID,
      credentials: {
        private_key: (vars.GCLOUD_LOGGING_PRIVATE_KEY || '').replace(/\\n/g, '\n'),
        client_email: vars.GCLOUD_LOGGING_CLIENT_EMAIL,
      },
    };

const loggingWinston = new LoggingWinston(googleCredential);

const enumerateErrorFormat = winston.format((info) => {
  if (info instanceof Error) {
    Object.assign(info, { message: info.stack });
  }
  if (typeof info === 'object' && info.level === 'error' && vars.env === 'production') {
    Object.assign(info, { message: `${vars.TE_APP_ENV} - ${info.message}${new Error().stack}` });
    // Add stack trace to error message to make sure picked up by Google Error Reporting
  }
  return info;
});

const logger = winston.createLogger({
  level: vars.env === 'development' ? 'debug' : 'info',
  format: winston.format.combine(
    enumerateErrorFormat(),
    vars.env === 'development' ? winston.format.colorize() : winston.format.uncolorize(),
    winston.format.splat(),
    winston.format.printf(({ level, message }) => `${level}: ${message}`),
  ),
  transports: compact([
    new winston.transports.Console({
      stderrLevels: ['error'],
    }),
    !['test', 'development'].includes(vars.env) && loggingWinston,
  ]),
});

logger.log = () => {
  throw new Error(
    'Cannot use logger.log directly. Use logger.info (or whatever level instead). You will also see this error if you try to log multiple items like logger.info("foo", "dahoo", data). That doesn not work either!',
  );
};

export default logger;

